if ( ! function_exists( 'gtg_load_fontawesome_icons' ) ) {
	/**
	 * Get Json Data.
	 *
	 * @return Array
	 * @since 1.0.0
	 */
	function gtg_load_fontawesome_icons() {
		if ( !function_exists( 'WP_Filesystem' ) ) {
			require_once ABSPATH . 'wp-admin/includes/file.php';
		}

		WP_Filesystem();
		global $wp_filesystem;
		$fontawesome_file = GTG_AB_DIR . 'assets/libs/fontawesome-icons.json';
		$icons = json_decode( $wp_filesystem->get_contents( $fontawesome_file ), true );
		return apply_filters( 'gutengeek_load_fontawesome_icons', $icons );
	}
}

if ( ! function_exists( 'gtg_sanitize_value' ) ) {
	function gtg_sanitize_value( $value ) {
		if ( is_string( $value ) ) {
			return $value;
		} else if ( is_numeric( $value ) ) {
			return sanitize_text_field( $value );
		} else if ( is_array( $value ) ) {
			foreach ( $value as $key => $val ) {
				$value[$key] = gtg_sanitize_value( $val );
			}
			return $value;
		}
		return $value;
	}
}

if ( ! function_exists( 'gtg_render_svg_html' ) ) {

	/**
	 * Generate SVG.
	 *
	 * @param array $icon Decoded fontawesome json file data.
	 * @return string
	 * @since 1.0.0
	 */
	function gtg_render_svg_html( $icon ) {
		$icon = str_replace( 'far', '', $icon );
		$icon = str_replace( 'fas', '', $icon );
		$icon = str_replace( 'fab', '', $icon );
		$icon = str_replace( 'fa-', '', $icon );
		$icon = str_replace( 'fa', '', $icon );

		$html = '';
		if ( is_string($icon) ) {
			$icon = sanitize_text_field( esc_attr( $icon ) );
		} else if ( is_array( $icon ) && isset( $icon['lib'] ) && ! empty( $icon['value'] )) {
			$icon_name = trim($icon['value']);
			$lib = $icon['lib'];
			if ($lib === 'fontawesome') {
				$json = gtg_load_fontawesome_icons();
				$path = isset( $json[ $icon_name ]['svg']['brands'] ) ? $json[ $icon_name ]['svg']['brands']['path'] : $json[ $icon_name ]['svg']['solid']['path'];
				$view = isset( $json[ $icon_name ], $json[ $icon_name ]['svg'], $json[ $icon_name ]['svg']['brands'], $json[ $icon_name ]['svg']['viewBox'] ) ? $json[ $icon_name ]['svg']['brands']['viewBox'] : $json[ $icon_name ]['svg']['solid']['viewBox'];
				if ( $view ) {
					$view = implode( ' ', $view );
				}
				$html = '<svg xmlns="http://www.w3.org/2000/svg" viewBox="' . $view . '"><path d="' . $path . '"></path></svg>';
			} else {
				$custom_icons = gtg_get_custom_icons();
				if ( ! empty($custom_icons[$lib]) && ! empty( $custom_icons[$lib]['icons'] ) && ! empty( $custom_icons[$lib]['icons'][$icon_name] )) {
					$html = ! empty( $custom_icons[$lib]['icons'][$icon_name]['svg'] ) ? $custom_icons[$lib]['icons'][$icon_name]['svg'] : '';
				}
			}
		}

		return apply_filters( 'gutengeek_render_svg_html', $html, $icon );
	}
}

if ( ! function_exists( 'gtg_get_image_sizes' ) ) {

	/**
	 * Get size information for all currently-registered image sizes.
	 *
	 * @return array $sizes Data for all currently-registered image sizes.
	 * @uses   get_intermediate_image_sizes()
	 * @link   https://codex.wordpress.org/Function_Reference/get_intermediate_image_sizes
	 * @since  1.9.0
	 * @global $_wp_additional_image_sizes
	 */
	function gtg_get_image_sizes() {

		global $_wp_additional_image_sizes;

		$sizes       = get_intermediate_image_sizes();
		$image_sizes = [];

		$image_sizes[] = [
			'value' => 'full',
			'label' => esc_html__( 'Full', 'gutengeek' ),
		];

		foreach ( $sizes as $size ) {
			if ( in_array( $size, [ 'thumbnail', 'medium', 'medium_large', 'large' ] ) ) {
				$image_sizes[] = [
					'value' => $size,
					'label' => ucwords( trim( str_replace( [ '-', '_' ], [ ' ', ' ' ], $size ) ) ),
				];
			} else {
				$image_sizes[] = [
					'value' => $size,
					'label' => sprintf(
						'%1$s (%2$sx%3$s)',
						ucwords( trim( str_replace( [ '-', '_' ], [ ' ', ' ' ], $size ) ) ),
						$_wp_additional_image_sizes[ $size ]['width'],
						$_wp_additional_image_sizes[ $size ]['height']
					),
				];
			}
		}

		return apply_filters( 'gutengeek_post_featured_image_sizes', $image_sizes );
	}
}


if ( ! function_exists( 'gtg_get_post_types' ) ) {

	/**
	 * Get Post Types.
	 *
	 * @since  1.0.0
	 * @access public
	 */
	function gtg_get_post_types() {

		$post_types = get_post_types(
			[
				'public'       => true,
				'show_in_rest' => true,
			],
			'objects'
		);

		$options = [];

		foreach ( $post_types as $post_type ) {
			if ( 'product' == $post_type->name ) {
				continue;
			}

			$options[] = [
				'value' => $post_type->name,
				'label' => $post_type->label,
			];
		}

		return apply_filters( 'gutengeek_loop_post_types', $options );
	}
}

if ( ! function_exists( 'gtg_get_related_taxonomy' ) ) {

	/**
	 * Get all taxonomies.
	 *
	 * @since  1.0.0
	 * @access public
	 */
	function gtg_get_related_taxonomy() {
		$post_types = gtg_get_post_types();

		$return_array = [];
		foreach ( $post_types as $key => $value ) {
			$post_type = $value['value'];

			$taxonomies = get_object_taxonomies( $post_type, 'objects' );
			$data       = [];

			foreach ( $taxonomies as $tax_slug => $tax ) {
				if ( ! $tax->public || ! $tax->show_ui ) {
					continue;
				}

				$data[ $tax_slug ] = $tax;

				$terms = get_terms( $tax_slug );

				$related_tax = [];

				if ( ! empty( $terms ) ) {
					foreach ( $terms as $t_index => $t_obj ) {
						$related_tax[] = [
							'id'   => $t_obj->term_id,
							'slug' => $t_obj->slug,
							'name' => $t_obj->name,
						];
					}

					$return_array[ $post_type ]['terms'][ $tax_slug ] = $related_tax;
				}
			}

			$return_array[ $post_type ]['taxonomy'] = $data;
		}

		return apply_filters( 'gutengeek_post_loop_taxonomies', $return_array );
	}
}

if ( ! function_exists( 'gtg_parse_blocks' ) ) {

	/**
	 * Parse Guten Block.
