if ( ! function_exists( 'gtg_build_query' ) ) {

	/**
	 * build WP_Query
	 *
	 * @param $attributes
	 * @param $block_type
	 * @return WP_Query
	 */
	function gtg_build_query( $attributes, $block_type ) {
		global $wp_query;

		$paged = ( get_query_var( 'paged' ) ) ? get_query_var( 'paged' ) : 1;
		$paged = isset( $wp_query->query['paged'] ) ? $wp_query->query['paged'] : $paged;
		$paged = ! empty( $_REQUEST['paged'] ) ? absint( $_REQUEST['paged'] ) : $paged;

		if ( isset( $_GET['paged'] ) && intval( $_GET['paged'] ) > 0 ) {
			$paged = intval( $_GET['paged'] );
		}

		$query_args = [
			'posts_per_page'      => ( isset( $attributes['numberOfPosts'] ) ) ? $attributes['numberOfPosts'] : 6,
			'paged'               => isset( $attributes['paged'] ) && $attributes['paged'] ? absint( $attributes['paged'] ) : $paged,
			'post_status'         => 'publish',
			'post_type'           => ( isset( $attributes['postType'] ) ) ? $attributes['postType'] : 'post',
			'order'               => ( isset( $attributes['order'] ) ) ? $attributes['order'] : 'desc',
			'orderby'             => ( isset( $attributes['orderBy'] ) ) ? $attributes['orderBy'] : 'date',
			'ignore_sticky_posts' => 1,
		];

		if ( isset( $attributes['categories'] ) && '' != $attributes['categories'] ) {
			$query_args['tax_query'][] = [
				'taxonomy' => isset( $attributes['taxonomyType'] ) ? $attributes['taxonomyType'] : 'category',
				'field'    => 'slug',
				'terms'    => $attributes['categories'],
				'operator' => 'IN',
			];
		}

		$query_args = apply_filters( "gutengeek_post_query_args_{$block_type}", $query_args );

		return new WP_Query( $query_args );
	}
}

if ( ! function_exists( 'gtg_get_option' ) ) {

	function gtg_get_option( $key, $default = false, $network_override = false ) {
		// is network
		if ( $network_override && is_multisite() ) {
			$value = get_site_option( $key, $default );
		} else {
			$value = get_option( $key, $default );
		}

		return apply_filters( 'gutengeek_get_option', $value, $key, $default, $network_override );
	}

}

if ( ! function_exists( 'gtg_update_option' ) ) {

	function gtg_update_option( $key, $value, $network = false ) {
		// Update the site-wide option since we're in the network admin.
		if ( $network && is_multisite() ) {
			update_site_option( $key, $value );
		} else {
			update_option( $key, $value );
		}
	}

}
