/**
 * Function Name: gutengeek_timeline_get_classes .
 *
 * @param array $attributes array of setting.
 * @return string             class name.
 */
function gtg_timeline_get_classes( $attributes ) {

	// Arrow position.
	$arrow_align_class = 'gutengeek-timeline-arrow-top' . ' ';
	if ( isset($attributes['arrowAlignment']) && 'center' === $attributes['arrowAlignment'] ) {
		$arrow_align_class = 'gutengeek-timeline-arrow-center' . ' ';
	} elseif ( isset($attributes['arrowAlignment']) && 'bottom' === $attributes['arrowAlignment'] ) {
		$arrow_align_class = 'gutengeek-timeline-arrow-bottom' . ' ';
	}

	// Alignmnet.
	$align_class = 'gutengeek-timeline-center-block ' . ' ';

	if ( isset($attributes['layout']) && 'left' === $attributes['layout'] ) {
		$align_class = 'gutengeek-timeline-left-block' . ' ';
	} elseif ( isset($attributes['layout']) && 'right' === $attributes['layout'] ) {
		$align_class = 'gutengeek-timeline-right-block' . ' ';
	}

	$stack = ! empty($attributes['stack']) ? $attributes['stack'] : 'tablet';
	$align_class .= $arrow_align_class . '';
	$align_class .= 'gutengeek-timeline-responsive-' . $stack . ' gutengeek-timeline';

	return $align_class;
}

/**
 * Function Name: gutengeek_timeline_get_align_classes description.
 *
 * @param array  $attributes attribute array.
 * @param string $index_val  post index.
 * @return string            output HTML/String.
 */
function gtg_timeline_get_align_classes( $attributes, $index_val ) {

	$align_class = '';
	$layout = ! empty($attributes['layout']) ? $attributes['layout'] : 'center';
	if ( 'left' === $layout ) {
		$align_class = 'gutengeek-timeline-item-inner gutengeek-timeline-left';
	} elseif ( 'right' === $layout ) {
		$align_class = 'gutengeek-timeline-item-inner gutengeek-timeline-right';
	} elseif ( 'center' == $layout ) {
		if ( 0 === $index_val % 2 ) {
			$align_class = 'gutengeek-timeline-item-inner gutengeek-timeline-right';
		} else {
			$align_class = 'gutengeek-timeline-item-inner gutengeek-timeline-left';
		}
	}

	return $align_class;
}

/**
 * Function Name: gutengeek_timeline_get_day_align_classes description.
 *
 * @param array  $attributes attribute array.
 * @param string $index_val  post index.
 * @return string            output HTML/String.
 */
function gtg_timeline_get_day_align_classes( $attributes, $index_val ) {

	$day_align_class = '';

	$layout = ! empty($attributes['layout']) ? $attributes['layout'] : 'center';
	if ( 'left' === $layout ) {
		$day_align_class = 'gutengeek-timeline-day-new gutengeek-timeline-day-left';
	} elseif ( 'right' === $layout ) {
		$day_align_class = 'gutengeek-timeline-day-new gutengeek-timeline-day-right';
	} elseif ( 'center' === $layout ) {
		if ( 0 === $index_val % 2 ) {
			$day_align_class = 'gutengeek-timeline-day-new gutengeek-timeline-day-right';
		} else {
			$day_align_class = 'gutengeek-timeline-day-new gutengeek-timeline-day-left';
		}
	}

	return $day_align_class;
}


if ( ! function_exists( 'gtg_get_static_css_file_path' ) ) {
	/**
	 * get static css file path
	 *
	 * @param null $post_id
	 * @return mixed|void
	 */
	function gtg_get_static_css_file_path( $post_id = null ) {
		$filename = GTG_AB_STATIC_CSS_PATH . $post_id . '.css';

		return apply_filters( 'gutengeek_post_css_file_path', $filename, $post_id );
	}
}

if ( ! function_exists( 'gtg_get_static_css_file_url' ) ) {
	/**
	 * get static css file url
	 *
	 * @param null $post_id
	 * @return mixed|void
	 */
	function gtg_get_static_css_file_url( $post_id = null ) {
		$file = gtg_get_static_css_file_path( $post_id );
		$url  = '';
		if ( file_exists( $file ) ) {
			$url = GTG_AB_STATIC_CSS_URL . $post_id . '.css';
		}

		return apply_filters( 'gutengeek_post_css_file_url', $url, $post_id );
	}
}

