if ( ! function_exists( 'gtg_render_shape_background' ) ) {

	/**
	 * print shape divider
	 */
	function gtg_render_shape_background( $name = '' ) {
		static $shapes;
		if ( empty( $shapes ) ) {
			$shapes = gtg_shape_backgrounds();
		}

		return ! empty( $shapes[ $name ] ) ? $shapes[ $name ] : '';
	}

}

