	/**
	 * sse emit message
	 */
	function gtg_emit_sse_event( $data = [] ) {
		if ( ! defined( 'WP_CLI' ) ) {
			echo "event: message\n";
			echo 'data: ' . wp_json_encode( $data ) . "\n\n";

			// Extra padding.
			echo ':' . str_repeat( ' ', 2048 ) . "\n\n";
		}

		flush();
	}

}

if ( ! function_exists( 'gtg_get_saved_items' ) ) {

	/**
	 * get all saved blocks items
	 */
	function gtg_get_saved_items() {
		$args = [
			'post_type' => 'wp_block',
			'post_status' => 'publish',
			'posts_per_page' => -1,
			'meta_query' => [
				'relation' => 'AND',
				[
					'key' => '_gutengeek_imported_id',
					'compare' => 'EXISTS'
				],
				[
					'key' => '_gutengeek_imported_type',
					'compare' => 'EXISTS'
				],
			]
		];

		$blocks = get_posts( apply_filters( 'gutengeek_get_saved_items_query', $args ) );
		$items = [];
		foreach ( $blocks as $block ) {
			$data = [
				'id' => get_post_meta( $block->ID, '_gutengeek_imported_id', true ),
				'local_id' => $block->ID,
				'type' => get_post_meta( $block->ID, '_gutengeek_imported_type', true ),
				'name' => $block->post_title,
				'source' => 'local',
				'content' => $block->post_content,
				'date' => date_i18n( get_option( 'date_format' ), strtotime( get_post_field( 'post_date', $block->ID ) ) ),
			];

			$items[] = $data;
		}

		return apply_filters( 'gutengeek_saved_blocks', $items );

	}

}

