	/**
	 * check remote post id is saved before?
	 *
	 * @return bool
	 */
	function gtg_is_remote_item_saved( $tmp_id = false ) {
		$args = [
			'post_type' => 'wp_block',
			'post_status' => 'publish',
			'posts_per_page' => -1,
			'meta_query' => [
