	}

}

if ( ! function_exists( 'gtg_get_custom_fonts' ) ) {

	/**
	 * get custom fonts uploaded before
	 *
	 * @since 1.0.0
	 * @return array
	 */
	function gtg_get_custom_fonts() {
		global $wpdb;
		$font_ids = $wpdb->get_col( $wpdb->prepare(
			"SELECT font_id FROM {$wpdb->prefix}gtg_block_fonts WHERE font_status = %s", 'publish'
		) );

		$font_data = [];
		foreach ($font_ids as $id) {
			$font_data[] = gtg_get_font_data($id);
		}
		return apply_filters( 'gutengeek_custom_fonts_loaded', $font_data );
	}

}

if ( ! function_exists( 'gtg_get_font_data' ) ) {

	/**
	 * get single font data
	 *
	 * @return mixed | array | WP_Error
	 */
	function gtg_get_font_data( $font_id = false ) {
		if ( ! $font_id ) {
			return $font_data = [];
		} else {
			global $wpdb;
			$query = $wpdb->prepare( "SELECT * FROM {$wpdb->prefix}gtg_block_fonts WHERE font_id = %d", $font_id );
			$font_data = $wpdb->get_row( $query, ARRAY_A );
			if ( ! $font_data ) {
				return new WP_Error( __( 'Font not found.' ) );
			}

			$query = $wpdb->prepare( "SELECT * FROM {$wpdb->prefix}gtg_block_font_items WHERE font_id = %d", $font_id );
			$font_items = $wpdb->get_results( $query, ARRAY_A );
			if ( $font_items ) {
				// each items
				foreach ( $font_items as $k => $font_item ) {
					$font_item_id = $font_item['font_item_id'];
					// each font files
					$query = $wpdb->prepare( "SELECT * FROM {$wpdb->prefix}gtg_block_font_item_attachments WHERE font_item_id = %d", $font_item_id );
					$files = $wpdb->get_results($query, ARRAY_A);
					foreach ( $files as $key => &$file ) {
						$font_attach_id = ! empty($file['font_attach_id']) ? $file['font_attach_id'] : '';
						$file['url'] = wp_get_attachment_url($font_attach_id);
					}
					$font_item['items'] = $files;
					$font_items[$k] = $font_item;
				}
				$font_data['items'] = $font_items;
			}
		}

		return apply_filters( 'gutengeek_font_data', $font_data, $font_id );
	}

}

